import React from "react";
import { Container, Nav, Navbar as BSNavbar, Button } from "react-bootstrap";
import { Link } from "react-router-dom";

function Navbar() {
  return (
    <BSNavbar bg="dark" variant="dark" expand="lg" className="shadow-sm" style={{ padding: "8px 0", position: "relative" }}>
      <Container fluid className="px-4">
        <BSNavbar.Brand as={Link} to="/" className="d-flex align-items-center">
          The Reading Nook
        </BSNavbar.Brand>

        {/* Nút Toggle cho mobile */}
        <BSNavbar.Toggle aria-controls="main-navbar" />

        {/* Phần nội dung có thể thu gọn, chứa các button */}
        <BSNavbar.Collapse id="main-navbar">
          {/* class 'ms-auto' sẽ đẩy cụm này sang bên phải */}
          <Nav className="ms-auto d-flex align-items-center gap-2">
            <Button
              variant="outline-light"
              // Bỏ className="me-2" nếu dùng gap-2 ở cha, hoặc giữ lại để chỉnh khoảng cách thủ công
              onClick={() => (window.location.href = "/login")}
              style={{ borderRadius: "20px" }}
            >
              Login
            </Button>

            <Button
              variant="light"
              style={{ borderRadius: "20px", color: "#333" }}
              onClick={() => (window.location.href = "/register")}
            >
              Register
            </Button>
          </Nav>
        </BSNavbar.Collapse>
      </Container>
    </BSNavbar>
  );
}

export default Navbar;
