import React from "react";
import { Card, Button, Badge } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

function BookCard({ book }) {
  const navigate = useNavigate();
  if (!book) return null;

  return (
    <Card className="h-100 shadow-sm">
      <div className="position-relative">
        {book.source === "bestseller" && (
          <Badge bg="danger" className="position-absolute m-2">
            Hot
          </Badge>
        )}
        {book.source === "new" && (
          <Badge bg="success" className="position-absolute m-2">
            New
          </Badge>
        )}
        <Card.Img
          variant="top"
          src={book.image || "https://via.placeholder.com/300x400?text=Book"}
          alt={book.title}
          style={{ height: "240px", objectFit: "cover" }}
        />
      </div>
      <Card.Body className="d-flex flex-column">
        <Card.Title style={{ fontSize: "1rem", minHeight: "48px" }}>
          {book.title}
        </Card.Title>
        <Card.Text className="text-muted mb-2" style={{ minHeight: "20px" }}>
          {book.author}
        </Card.Text>
        <div className="mt-auto">
          <div className="fw-bold mb-3">
            {book.price ? `${book.price.toLocaleString()} VND` : "Contact"}
          </div>
          <Button
            variant="dark"
            className="w-100"
            onClick={() => navigate(`/product/${book.id}`)}
          >
            View Detail
          </Button>
        </div>
      </Card.Body>
    </Card>
  );
}

export default BookCard;
