import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Container, Row, Col, Button, Card, Form } from "react-bootstrap";
import Navbar from "../components/Navbar";

// 1. Tạo dữ liệu giả (Mock Data) để mô phỏng Database
// Trong thực tế, bạn sẽ thay thế cái này bằng việc gọi API
const mockBooks = [
  {
    id: 1,
    title: "The Great Gatsby",
    author: "F. Scott Fitzgerald",
    price: 150000,
    originalPrice: 200000,
    discount: 25,
    rating: 4.5,
    reviews: 120,
    description: "The Great Gatsby is a 1925 novel by American writer F. Scott Fitzgerald. Set in the Jazz Age on Long Island, near New York City, the novel depicts first-person narrator Nick Carraway's interactions with mysterious millionaire Jay Gatsby and Gatsby's obsession to reunite with his former lover, Daisy Buchanan.",
    image: "https://upload.wikimedia.org/wikipedia/commons/7/7a/The_Great_Gatsby_Cover_1925_Retouched.jpg"
  },
  {
    id: 2,
    title: "To Kill a Mockingbird",
    author: "Harper Lee",
    price: 180000,
    originalPrice: null,
    discount: null,
    rating: 5,
    reviews: 200,
    description: "To Kill a Mockingbird is a novel by the American author Harper Lee. It was published in 1960 and was instantly successful. In the United States, it is widely read in high schools and middle schools. To Kill a Mockingbird has become a classic of modern American literature.",
    image: "https://upload.wikimedia.org/wikipedia/commons/4/4f/To_Kill_a_Mockingbird_%28first_edition_cover%29.jpg"
  }
];

function ProductDetail() {
  const navigate = useNavigate();
  // 2. Lấy ID từ URL
  const { id } = useParams();

  const [book, setBook] = useState(null);
  const [feedbacks, setFeedbacks] = useState([]);
  const [rate, setRate] = useState(5);
  const [comment, setComment] = useState("");
  const [name, setName] = useState("");

  // 3. Dùng useEffect để tìm sách khi component được load hoặc ID thay đổi
  useEffect(() => {
    // Tìm sách trong mảng mockBooks dựa vào ID trên URL
    // Lưu ý: id từ URL là chuỗi, cần chuyển sang số (parseInt) nếu id trong data là số
    const foundBook = mockBooks.find((b) => b.id === parseInt(id));

    if (foundBook) {
      setBook(foundBook);
    } else {
      setBook(null); // Không tìm thấy sách
    }

    // Reset feedback khi đổi sách (nếu muốn)
    setFeedbacks([]);
  }, [id]);

  const handleSubmitFeedback = (e) => {
    e.preventDefault();
    const newFeedback = {
      id: Date.now(),
      userName: name || "Anonymous",
      rate: parseFloat(rate),
      comment: comment,
      createdAt: new Date().toISOString()
    };
    setFeedbacks([newFeedback, ...feedbacks]);
    setComment("");
    setName("");
    setRate(5);
  };

  // 4. Màn hình chờ hoặc thông báo lỗi nếu chưa có dữ liệu sách
  if (!book) {
    return (
      <>
        <Navbar />
        <Container style={{ padding: "100px 0", textAlign: "center" }}>
          <h3>Book not found or Loading...</h3>
          <Button variant="dark" onClick={() => navigate("/")}>Go Home</Button>
        </Container>
      </>
    );
  }

  return (
    <>
      <Navbar />

      <section style={{ padding: "80px 0" }}>
        <Container>
          <Row>
            <Col md={5} className="text-center mb-4">
              <Card className="border-0 shadow-sm">
                <Card.Img
                  variant="top"
                  src={book.image} // Thêm src vào đây
                  alt={book.title}
                  style={{
                    maxHeight: "400px",
                    objectFit: "contain",
                    padding: "20px",
                  }}
                />
              </Card>
            </Col>

            <Col md={7}>
              <h2 style={{ fontWeight: "700" }}>{book.title}</h2>
              <p style={{ color: "#7f8c8d" }}>{book.author}</p>

              {book.rating && (
                <div className="d-flex align-items-center mb-3">
                  <span style={{ color: "#f1c40f", fontSize: "1.2rem" }}>
                    {"★".repeat(Math.round(book.rating))}
                    {"☆".repeat(5 - Math.round(book.rating))}
                  </span>
                  <span className="ms-2 text-muted">
                    {book.rating} / 5 ({book.reviews} reviews)
                  </span>
                </div>
              )}

              <div className="mt-3 mb-3">
                {book.originalPrice && (
                  <div>
                    <span className="text-muted text-decoration-line-through">
                      {book.originalPrice.toLocaleString()} VND
                    </span>
                    {book.discount && (
                      <span className="ms-2 px-2 py-1 bg-danger text-white rounded" style={{ fontSize: "0.9rem", fontWeight: "600" }}>
                        -{book.discount}%
                      </span>
                    )}
                  </div>
                )}
                <h3 className="mt-2 text-danger fw-bold">
                  {book.price?.toLocaleString()} VND
                </h3>
              </div>

              <p className="mt-4">{book.description}</p>

              <div className="mt-4">
                <Button variant="dark" size="lg" style={{ borderRadius: "30px", padding: "10px 24px" }}>
                  <i className="fas fa-cart-plus me-2"></i>
                  Add to Cart
                </Button>
                <Button
                  variant="outline-dark"
                  size="lg"
                  style={{ borderRadius: "30px", padding: "10px 24px", marginLeft: "12px" }}
                  // Sửa lại đường dẫn này cho đúng với route của bạn
                  onClick={() => navigate("/")}
                >
                  Back to List
                </Button>
              </div>
            </Col>
          </Row>
        </Container>
      </section>

      <section style={{ padding: "20px 0 60px 0" }}>
        <Container>
          <Row>
            <Col md={12}>
              <h4 className="mb-3">Feedback</h4>
              {feedbacks.length === 0 ? (
                <p className="text-muted">No feedback yet.</p>
              ) : (
                feedbacks.map((fb) => (
                  <Card className="mb-2" key={fb.id}>
                    <Card.Body>
                      <div className="d-flex justify-content-between">
                        <div>
                          <strong>{fb.userName}</strong>{" "}
                          <span className="text-muted" style={{ fontSize: "0.9rem" }}>
                            {new Date(fb.createdAt).toLocaleString()}
                          </span>
                        </div>
                        <div style={{ color: "#f1c40f" }}>
                          {"★".repeat(Math.round(fb.rate || 0))}
                          {"☆".repeat(5 - Math.round(fb.rate || 0))}
                        </div>
                      </div>
                      <div className="mt-2">{fb.comment}</div>
                    </Card.Body>
                  </Card>
                ))
              )}
            </Col>
          </Row>

          <Row className="mt-4">
            <Col md={12}>
              <Card className="p-3">
                <h5 className="mb-3">Add your feedback</h5>
                <Form onSubmit={handleSubmitFeedback}>
                  <Row className="g-3">
                    <Col md={3}>
                      <Form.Group controlId="name">
                        <Form.Label>Name (optional)</Form.Label>
                        <Form.Control
                          type="text"
                          placeholder="Your name"
                          value={name}
                          onChange={(e) => setName(e.target.value)}
                        />
                      </Form.Group>
                    </Col>
                    <Col md={3}>
                      <Form.Group controlId="rate">
                        <Form.Label>Rate</Form.Label>
                        <Form.Select value={rate} onChange={(e) => setRate(e.target.value)}>
                          <option value={5}>5 - Excellent</option>
                          <option value={4}>4 - Good</option>
                          <option value={3}>3 - Average</option>
                          <option value={2}>2 - Poor</option>
                          <option value={1}>1 - Terrible</option>
                        </Form.Select>
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="comment">
                        <Form.Label>Comment</Form.Label>
                        <Form.Control
                          as="textarea"
                          rows={2}
                          placeholder="Share your thoughts about this book"
                          value={comment}
                          onChange={(e) => setComment(e.target.value)}
                          required
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                  <div className="mt-3">
                    <Button type="submit" variant="dark">
                      Submit Feedback
                    </Button>
                  </div>
                </Form>
              </Card>
            </Col>
          </Row>
        </Container>
      </section>

      {/* Footer giữ nguyên như cũ */}
      <footer style={{ backgroundColor: "#000", color: "white", padding: "40px 0" }}>
        {/* ... (Nội dung footer của bạn) ... */}
        <Container>
          <div className="text-center">
            <p style={{ margin: 0, fontSize: "14px", color: "#e9ecef" }}>
              © 2025 The Reading Nook. All rights reserved.
            </p>
          </div>
        </Container>
      </footer>
    </>
  );
}

export default ProductDetail;
