function ProductList() {
  const [products, setProducts] = useState([]); // D·ªØ li·ªáu g·ªëc
  const [filteredProducts, setFilteredProducts] = useState([]); // D·ªØ li·ªáu sau khi l·ªçc

  // State ƒë·ªÉ t·∫°o danh s√°ch options cho select box
  const [categories, setCategories] = useState([]);
  const [brands, setBrands] = useState([]);

  const [filters, setFilters] = useState({
    category: "",
    brand: "", // ƒê·ªïi author -> brand
    priceRange: "",
  });
  const [sortBy, setSortBy] = useState("default");

  // 1. Kh·ªüi t·∫°o d·ªØ li·ªáu ban ƒë·∫ßu
  useEffect(() => {
    // Trong th·ª±c t·∫ø, ch·ªó n√†y s·∫Ω g·ªçi API
    setProducts(mockAllProducts);
    setFilteredProducts(mockAllProducts);

    // L·∫•y danh s√°ch category v√† brand duy nh·∫•t t·ª´ d·ªØ li·ªáu
    const uniqueCats = [...new Set(mockAllProducts.map(p => p.category))];
    const uniqueBrands = [...new Set(mockAllProducts.map(p => p.brand))];
    setCategories(uniqueCats);
    setBrands(uniqueBrands);
  }, []);

  // 2. X·ª≠ l√Ω Logic L·ªçc & S·∫Øp x·∫øp
  useEffect(() => {
    let result = [...products];

    // --- FILTER ---
    if (filters.category) {
      result = result.filter(p => p.category === filters.category);
    }
    if (filters.brand) {
      result = result.filter(p => p.brand === filters.brand);
    }
    if (filters.priceRange) {
      if (filters.priceRange === "low") { // D∆∞·ªõi 10 tri·ªáu
        result = result.filter(p => p.price < 10000000);
      } else if (filters.priceRange === "mid") { // 10 - 25 tri·ªáu
        result = result.filter(p => p.price >= 10000000 && p.price <= 25000000);
      } else if (filters.priceRange === "high") { // Tr√™n 25 tri·ªáu
        result = result.filter(p => p.price > 25000000);
      }
    }

    // --- SORT ---
    if (sortBy === "priceAsc") {
      result.sort((a, b) => a.price - b.price);
    } else if (sortBy === "priceDesc") {
      result.sort((a, b) => b.price - a.price);
    } else if (sortBy === "newest") {
      // Gi·∫£ l·∫≠p sort theo ID gi·∫£m d·∫ßn (m·ªõi nh·∫•t l√™n ƒë·∫ßu)
      result.sort((a, b) => b.id - a.id);
    }

    setFilteredProducts(result);
  }, [filters, sortBy, products]);

  return (
    <div style={{ backgroundColor: "#f4f6f8", minHeight: "100vh" }}>
      <Navbar />

      <Container style={{ padding: "40px 0" }}>
        <Row>
          {/* üéöÔ∏è Sidebar Filter */}
          <Col md={3} className="mb-4">
            <div className="p-4 shadow-sm rounded bg-white sticky-top" style={{ top: "90px" }}>
              <h5 className="fw-bold mb-4 d-flex align-items-center">
                <i className="fas fa-filter me-2 text-primary"></i> Filters
              </h5>

              {/* Danh m·ª•c */}
              <Form.Group className="mb-3">
                <Form.Label className="fw-semibold">Category</Form.Label>
                <Form.Select
                  value={filters.category}
                  onChange={(e) => setFilters({ ...filters, category: e.target.value })}
                >
                  <option value="">All Categories</option>
                  {categories.map((cat, index) => (
                    <option key={index} value={cat}>{cat}</option>
                  ))}
                </Form.Select>
              </Form.Group>

              {/* Th∆∞∆°ng hi·ªáu (Thay Author -> Brand) */}
              <Form.Group className="mb-3">
                <Form.Label className="fw-semibold">Brand</Form.Label>
                <Form.Select
                  value={filters.brand}
                  onChange={(e) => setFilters({ ...filters, brand: e.target.value })}
                >
                  <option value="">All Brands</option>
                  {brands.map((brand, index) => (
                    <option key={index} value={brand}>{brand}</option>
                  ))}
                </Form.Select>
              </Form.Group>

              {/* Kho·∫£ng gi√° (C·∫≠p nh·∫≠t gi√° Tech) */}
              <Form.Group className="mb-4">
                <Form.Label className="fw-semibold">Price Range</Form.Label>
                <Form.Select
                  value={filters.priceRange}
                  onChange={(e) => setFilters({ ...filters, priceRange: e.target.value })}
                >
                  <option value="">All Prices</option>
                  <option value="low">Under 10 Million</option>
                  <option value="mid">10 - 25 Million</option>
                  <option value="high">Above 25 Million</option>
                </Form.Select>
              </Form.Group>

              <Button
                variant="dark"
                className="w-100"
                onClick={() => setFilters({ category: "", brand: "", priceRange: "" })}
              >
                Reset Filters
              </Button>
            </div>
          </Col>

          {/* üì± Product List */}
          <Col md={9}>
            <div className="d-flex justify-content-between align-items-center mb-4 p-3 bg-white rounded shadow-sm">
              <h4 className="fw-bold m-0">
                All Products <span className="text-muted fs-6">({filteredProducts.length} items)</span>
              </h4>
              <Form.Select
                style={{ width: "200px", border: "1px solid #ced4da" }}
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
              >
                <option value="default">Sort by: Default</option>
                <option value="newest">Newest Arrivals</option>
                <option value="priceAsc">Price: Low to High</option>
                <option value="priceDesc">Price: High to Low</option>
              </Form.Select>
            </div>

            <Row>
              {filteredProducts.length > 0 ? (
                filteredProducts.map((product) => (
                  <Col lg={4} md={6} className="mb-4" key={product.id}>
                    {/* S·ª≠ d·ª•ng component con ProductCard */}
                    <ProductCard product={product} />
                  </Col>
                ))
              ) : (
                <Col md={12}>
                  <div className="text-center py-5 bg-white rounded shadow-sm">
                    <i className="fas fa-search fa-3x text-muted mb-3"></i>
                    <h5>No products found matching your criteria.</h5>
                    <p className="text-muted">Try removing some filters.</p>
                    <Button
                      variant="outline-primary"
                      onClick={() => setFilters({ category: "", brand: "", priceRange: "" })}
                    >
                      Clear All Filters
                    </Button>
                  </div>
                </Col>
              )}
            </Row>
          </Col>
        </Row>
      </Container>

      {/* ü¶∂ Footer TechZone */}
      <footer style={{ backgroundColor: "#111", color: "white", padding: "40px 0" }}>
        <Container>
          <Row>
            <Col md={6}>
              <h5 className="fw-bold d-flex align-items-center">
                <i className="fas fa-microchip me-2" style={{ color: "#00c3ff" }}></i>
                TechZone
              </h5>
              <p className="text-secondary small">
                Vietnam's leading tech retailer. Authentic products, premium service.
              </p>
            </Col>
            <Col md={6} className="text-md-end">
              <p className="mb-1 text-secondary small">¬© 2025 TechZone. All rights reserved.</p>
            </Col>
          </Row>
        </Container>
      </footer>
    </div>
  );
}

