import React, { useState } from "react";
import { Container, Row, Col, Card, Form, Button } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";

function Login() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: "", password: "", rememberMe: false });
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({ ...prev, [name]: type === "checkbox" ? checked : value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // UI-only flow: simply navigate to home
    alert("Logged in (demo).");
    navigate("/");
  };

  return (
    <div style={{
      minHeight: "100vh",
      backgroundColor: "#f8f9fa",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      padding: "20px 0"
    }}>
      <Container>
        <Row className="justify-content-center">
          <Col lg={5} md={7} sm={9}>
            <div className="text-center mb-4">
              <div className="mb-3">
                <div style={{
                  width: "80px",
                  height: "80px",
                  backgroundColor: "#2c3e50",
                  borderRadius: "16px",
                  margin: "0 auto",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center"
                }}>
                  <i className="fas fa-book" style={{ color: "white", fontSize: "32px" }}></i>
                </div>
              </div>

              {/* Title */}
              <h2 style={{
                fontSize: "28px",
                fontWeight: "700",
                color: "#2c3e50",
                marginBottom: "8px"
              }}>
                Book Library
              </h2>

              <p style={{
                fontSize: "14px",
                color: "#6c757d",
                marginBottom: "8px"
              }}>
                ðŸ“– "Books are the door to the world of knowledge" ðŸ“–
              </p>

              {/* Description */}
              <p style={{
                fontSize: "14px",
                color: "#6c757d"
              }}>
                Login to explore the endless treasure of knowledge
              </p>
            </div>

            {/* Login Card */}
            <Card className="shadow-lg border-0" style={{ borderRadius: "16px" }}>
              <Card.Body style={{ padding: "40px" }}>
                {/* Login Header */}
                <div className="text-center mb-4">
                  <h4 style={{
                    fontSize: "20px",
                    fontWeight: "600",
                    color: "#2c3e50",
                    marginBottom: "8px"
                  }}>
                    ðŸ‘‹ Login âœ¨
                  </h4>
                  <p style={{
                    fontSize: "14px",
                    color: "#6c757d",
                    margin: 0
                  }}>
                    Enter your information to access your account
                  </p>
                </div>


                {/* Login Form */}
                <Form onSubmit={handleSubmit}>
                  {/* Email Field */}
                  <Form.Group className="mb-3">
                    <Form.Label style={{ fontWeight: "500", color: "#2c3e50" }}>
                      Email Address
                    </Form.Label>
                    <div className="position-relative">
                      <i
                        className="fas fa-envelope position-absolute"
                        style={{
                          left: "12px",
                          top: "50%",
                          transform: "translateY(-50%)",
                          color: "#6c757d",
                          zIndex: 10
                        }}
                      ></i>
                      <Form.Control
                        type="email"
                        name="email"
                        placeholder="your@email.com"
                        onChange={handleChange}
                        required
                        style={{
                          paddingLeft: "40px",
                          height: "48px",
                          borderRadius: "8px",
                          border: "1px solid #e9ecef",
                          fontSize: "14px"
                        }}
                      />
                    </div>
                  </Form.Group>

                  {/* Password Field */}
                  <Form.Group className="mb-3">
                    <Form.Label style={{ fontWeight: "500", color: "#2c3e50" }}>
                      Password
                    </Form.Label>
                    <div className="position-relative">
                      <i
                        className="fas fa-lock position-absolute"
                        style={{
                          left: "12px",
                          top: "50%",
                          transform: "translateY(-50%)",
                          color: "#6c757d",
                          zIndex: 10
                        }}
                      ></i>
                  <Form.Control
                    type={showPassword ? "text" : "password"}
                    name="password"
                    placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                    value={formData.password}
                    onChange={handleChange}
                    required
                    style={{
                      paddingLeft: "40px",
                      paddingRight: "40px",
                      height: "48px",
                      borderRadius: "8px",
                      border: "1px solid #e9ecef",
                      fontSize: "14px"
                    }}
                  />
                      <Button
                        variant="link"
                        type="button"
                    onClick={() => setShowPassword(!showPassword)}
                        style={{
                          position: "absolute",
                          right: "8px",
                          top: "50%",
                          transform: "translateY(-50%)",
                          border: "none",
                          background: "none",
                          color: "#6c757d",
                          padding: "4px 8px"
                        }}
                      >
                        <i className={`fas fa-eye${showPassword ? '-slash' : ''}`}></i>
                      </Button>
                    </div>
                  </Form.Group>

                  {/* Remember Me & Forgot Password */}
                  <div className="d-flex justify-content-between align-items-center mb-4">
                    <Form.Check
                      type="checkbox"
                      name="rememberMe"
                      label="Remember me"
                      onChange={handleChange}
                      style={{ fontSize: "14px", color: "#6c757d" }}
                    />
                    <Link
                      to="/forgotpassword"
                      style={{
                        fontSize: "14px",
                        color: "#007bff",
                        textDecoration: "none"
                      }}
                    >
                      Forgot password?
                    </Link>
                  </div>

                  {/* Login Button */}
                  <Button
                    type="submit"
                    style={{
                      width: "100%",
                      height: "48px",
                      backgroundColor: "#2c3e50",
                      border: "none",
                      borderRadius: "8px",
                      fontSize: "16px",
                      fontWeight: "600",
                      marginBottom: "20px"
                    }}
                  >

                  </Button>

                  {/* OR Divider */}
                  <div className="text-center mb-3">
                    <span style={{
                      fontSize: "14px",
                      color: "#6c757d",
                      backgroundColor: "#fff",
                      padding: "0 16px"
                    }}>
                      OR LOGIN WITH
                    </span>
                  </div>

                  {/* Social Login Buttons */}
                  <Row className="mb-4">
                    <Col xs={6}>
                      <Button
                        variant="outline-secondary"
                        style={{
                          width: "100%",
                          height: "48px",
                          borderRadius: "8px",
                          fontSize: "14px",
                          fontWeight: "500"
                        }}
                      >
                        <i className="fab fa-google me-2"></i>
                        Google
                      </Button>
                    </Col>
                    <Col xs={6}>
                      <Button
                        variant="outline-primary"

                        style={{
                          width: "100%",
                          height: "48px",
                          borderRadius: "8px",
                          fontSize: "14px",
                          fontWeight: "500"
                        }}
                      >
                        <i className="fab fa-facebook-f me-2"></i>
