import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Container, Row, Col, Button, Card, Form } from "react-bootstrap";
import Navbar from "../components/Navbar";

// 1. CHUẨN BỊ DỮ LIỆU GIẢ (Phải khớp ID với bên trang Home)
const mockBooks = [
  {
    id: 1,
    title: "The Great Gatsby",
    author: "F. Scott Fitzgerald",
    price: 150000,
    originalPrice: 200000,
    discount: 25,
    rating: 4.5,
    reviews: 120,
    description: "The Great Gatsby is a 1925 novel by American writer F. Scott Fitzgerald. Set in the Jazz Age on Long Island...",
    image: "https://upload.wikimedia.org/wikipedia/commons/7/7a/The_Great_Gatsby_Cover_1925_Retouched.jpg"
  },
  {
    id: 2,
    title: "To Kill a Mockingbird",
    author: "Harper Lee",
    price: 180000,
    originalPrice: null,
    discount: null,
    rating: 5,
    reviews: 200,
    description: "To Kill a Mockingbird is a novel by the American author Harper Lee. It was published in 1960 and was instantly successful...",
    image: "https://upload.wikimedia.org/wikipedia/commons/4/4f/To_Kill_a_Mockingbird_%28first_edition_cover%29.jpg"
  },
  {
    id: 3,
    title: "1984",
    author: "George Orwell",
    price: 120000,
    originalPrice: 150000,
    discount: 20,
    rating: 4.8,
    reviews: 315,
    description: "Nineteen Eighty-Four is a dystopian social science fiction novel and cautionary tale written by English novelist George Orwell.",
    image: "https://m.media-amazon.com/images/I/71rpa1-kyvL._AC_UF1000,1000_QL80_.jpg"
  },
  {
    id: 4,
    title: "Pride and Prejudice",
    author: "Jane Austen",
    price: 90000,
    originalPrice: 100000,
    discount: 10,
    rating: 4.2,
    reviews: 98,
    description: "Pride and Prejudice is an 1813 novel of manners by Jane Austen. The novel follows the character development of Elizabeth Bennet.",
    image: "https://m.media-amazon.com/images/I/71Q1tPupKjL._AC_UF1000,1000_QL80_.jpg"
  },
];

function ProductDetail() {
  const navigate = useNavigate();

  // 2. LẤY ID TỪ URL
  const { id } = useParams();

  // State để lưu sách tìm được
  const [book, setBook] = useState(null);

  // State cho phần feedback
  const [feedbacks, setFeedbacks] = useState([]);
  const [name, setName] = useState("");
  const [rate, setRate] = useState(5);
  const [comment, setComment] = useState("");

  // 3. TÌM SÁCH KHI ID THAY ĐỔI
  useEffect(() => {
    // id lấy từ URL là string, cần đổi sang number để so sánh
    const foundBook = mockBooks.find((b) => b.id === parseInt(id));

    if (foundBook) {
      setBook(foundBook);
    } else {
      setBook(null); // Không tìm thấy
    }
    setFeedbacks([]); // Reset comment khi đổi sách
  }, [id]);

  // Xử lý gửi feedback
  const handleSubmitFeedback = (e) => {
    e.preventDefault();
    const newFeedback = {
      id: Date.now(),
      userName: name || "Anonymous",
      rate: parseInt(rate),
      comment: comment,
      createdAt: new Date()
    };
    setFeedbacks([newFeedback, ...feedbacks]);
    setComment("");
    setName("");
    setRate(5);
  };

  // Nếu không tìm thấy sách hoặc đang load
  if (!book) {
    return (
      <>
        <Navbar />
        <Container style={{ padding: "100px 0", textAlign: "center" }}>
          <h3>Product not found!</h3>
          <Button variant="dark" onClick={() => navigate("/")}>Back to Home</Button>
        </Container>
      </>
    );
  }

  return (
    <>
      <Navbar />

      <section style={{ padding: "80px 0" }}>
        <Container>
          <Row>
            {/* Cột Trái: Ảnh Sách */}
            <Col md={5} className="text-center mb-4">
              <Card className="border-0 shadow-sm">
                <Card.Img
                  variant="top"
                  src={book.image} // 4. HIỂN THỊ ẢNH
                  alt={book.title}
                  style={{
                    maxHeight: "400px",
                    objectFit: "contain",
                    padding: "20px",
                  }}
                />
              </Card>
            </Col>

            {/* Cột Phải: Thông tin chi tiết */}
            <Col md={7}>
              <h2 style={{ fontWeight: "700" }}>{book.title}</h2>
              <p style={{ color: "#7f8c8d", fontSize: "1.2rem" }}>by {book.author}</p>

              {/* Rating stars */}
              <div className="d-flex align-items-center mb-3">
                <span style={{ color: "#f1c40f", fontSize: "1.2rem" }}>
                  {"★".repeat(Math.round(book.rating))}
                  {"☆".repeat(5 - Math.round(book.rating))}
                </span>
                <span className="ms-2 text-muted">
                  ({book.reviews} reviews)
                </span>
              </div>

              {/* Giá tiền */}
              <div className="mt-3 mb-3">
                {book.originalPrice && (
                  <div>
                    <span className="text-muted text-decoration-line-through">
                      {book.originalPrice.toLocaleString()} VND
                    </span>
                    {book.discount && (
                      <span className="ms-2 px-2 py-1 bg-danger text-white rounded" style={{ fontSize: "0.9rem", fontWeight: "600" }}>
                        -{book.discount}%
                      </span>
                    )}
                  </div>
                )}
                <h3 className="mt-2 text-danger fw-bold">
                  {book.price.toLocaleString()} VND
                </h3>
              </div>

              <p className="mt-4" style={{ lineHeight: "1.6" }}>{book.description}</p>

              <div className="mt-4">
                <Button variant="dark" size="lg" style={{ borderRadius: "30px", padding: "10px 30px" }}>
                  <i className="fas fa-cart-plus me-2"></i>
                  Add to Cart
                </Button>
                <Button
                  variant="outline-dark"
                  size="lg"
                  style={{ borderRadius: "30px", padding: "10px 30px", marginLeft: "12px" }}
                  onClick={() => navigate("/")}
                >
                  Continue Shopping
                </Button>
              </div>
            </Col>
          </Row>
        </Container>
      </section>

      {/* Phần Feedback */}
      <section style={{ padding: "20px 0 60px 0", backgroundColor: "#f9f9f9" }}>
        <Container>
          <Row>
            <Col md={12}>
              <h4 className="mb-4">Customer Feedback</h4>
              {/* Hiển thị list feedback */}
              {feedbacks.length === 0 ? (
                <p className="text-muted mb-4">No reviews yet. Be the first to review!</p>
              ) : (
                feedbacks.map((fb) => (
                  <Card className="mb-3 border-0 shadow-sm" key={fb.id}>
                    <Card.Body>
                      <div className="d-flex justify-content-between">
                        <h6 className="fw-bold">{fb.userName}</h6>
                        <span style={{ color: "#f1c40f" }}>
                          {"★".repeat(fb.rate)}
                          {"☆".repeat(5 - fb.rate)}
                        </span>
                      </div>
                      <p className="mb-0 text-secondary">{fb.comment}</p>
                      <small className="text-muted">{fb.createdAt.toLocaleString()}</small>
                    </Card.Body>
                  </Card>
                ))
              )}
            </Col>
          </Row>

          <Row className="mt-2">
            <Col md={12}>
              <Card className="p-4 border-0 shadow-sm">
                <h5 className="mb-3">Write a Review</h5>
                <Form onSubmit={handleSubmitFeedback}>
                  <Row className="g-3">
                    <Col md={6}>
                      <Form.Group controlId="name">
                        <Form.Label>Name (optional)</Form.Label>
                        <Form.Control
                          type="text"
                          placeholder="Your name"
                          value={name}
                          onChange={(e) => setName(e.target.value)}
                        />
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="rate">
                        <Form.Label>Rating</Form.Label>
                        <Form.Select value={rate} onChange={(e) => setRate(e.target.value)}>
                          <option value={5}>5 - Excellent</option>
                          <option value={4}>4 - Good</option>
                          <option value={3}>3 - Average</option>
                          <option value={2}>2 - Poor</option>
                          <option value={1}>1 - Terrible</option>
                        </Form.Select>
                      </Form.Group>
                    </Col>
                    <Col md={12}>
                      <Form.Group controlId="comment">
                        <Form.Label>Comment</Form.Label>
                        <Form.Control
                          as="textarea"
                          rows={3}
                          placeholder="Share your thoughts about this book"
                          value={comment}
                          onChange={(e) => setComment(e.target.value)}
                          required
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                  <div className="mt-3 text-end">
                    <Button type="submit" variant="dark" className="px-4">
                      Submit Feedback
                    </Button>
                  </div>
                </Form>
              </Card>
            </Col>
          </Row>
        </Container>
      </section>

      <footer style={{ backgroundColor: "#000", color: "white", padding: "40px 0" }}>
        {/* Giữ nguyên phần footer cũ của bạn */}
        <Container>
          <div className="text-center">
            <p>© 2025 The Reading Nook</p>
          </div>
        </Container>
      </footer>
    </>
  );
}

export default ProductDetail;
