import React, { useEffect, useState } from "react";
import { Container, Row, Col, Form, Button, Card, Badge } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Footer from "../components/footer";

const mockAllProducts = [
  { id: 1, title: "MacBook Pro M3 Max", brand: "Apple", category: "Laptop", price: 45000000, rating: 5.0, image: "https://images.unsplash.com/photo-1517336714731-489689fd1ca4?q=80&w=1000&auto=format&fit=crop" },
  { id: 2, title: "Samsung Galaxy S24 Ultra", brand: "Samsung", category: "Smartphone", price: 32000000, rating: 4.8, image: "https://images.unsplash.com/photo-1610945415295-d9bbf067e59c?q=80&w=1000&auto=format&fit=crop" },
  { id: 3, title: "Sony WH-1000XM5", brand: "Sony", category: "Audio", price: 8500000, rating: 4.7, image: "https://images.unsplash.com/photo-1618366712010-f4ae9c647dcb?q=80&w=1000&auto=format&fit=crop" },
  { id: 4, title: "iPad Pro 12.9 M2", brand: "Apple", category: "Tablet", price: 28000000, rating: 4.9, image: "https://images.unsplash.com/photo-1544244015-0df4b3ffc6b0?q=80&w=1000&auto=format&fit=crop" },
  { id: 5, title: "Dell XPS 15", brand: "Dell", category: "Laptop", price: 38000000, rating: 4.6, image: "https://images.unsplash.com/photo-1593642632823-8f7856677741?q=80&w=1000&auto=format&fit=crop" },
  { id: 6, title: "iPhone 15 Pro Max", brand: "Apple", category: "Smartphone", price: 34000000, rating: 4.9, image: "https://images.unsplash.com/photo-1696426718961-e72e12895171?q=80&w=1000&auto=format&fit=crop" },
  { id: 7, title: "Asus ROG Strix", brand: "Asus", category: "Laptop", price: 25000000, rating: 4.5, image: "https://images.unsplash.com/photo-1603302576837-37561b2e2302?q=80&w=1000&auto=format&fit=crop" },
  { id: 8, title: "AirPods Pro 2", brand: "Apple", category: "Audio", price: 5500000, rating: 4.8, image: "https://images.unsplash.com/photo-1600294037681-c80b4cb5b434?q=80&w=1000&auto=format&fit=crop" },
];



function ProductList() {
  const [products, setProducts] = useState([]); // D·ªØ li·ªáu g·ªëc
  const [filteredProducts, setFilteredProducts] = useState([]); // D·ªØ li·ªáu sau khi l·ªçc

  // State ƒë·ªÉ t·∫°o danh s√°ch options cho select box
  const [categories, setCategories] = useState([]);
  const [brands, setBrands] = useState([]);

  const [filters, setFilters] = useState({
    category: "",
    brand: "", // ƒê·ªïi author -> brand
    priceRange: "",
  });
  const [sortBy, setSortBy] = useState("default");



  return (
    <div style={{ backgroundColor: "#f4f6f8", minHeight: "100vh" }}>
      <Navbar />

      <Container style={{ padding: "40px 0" }}>
        <Row>
          {/* üéöÔ∏è Sidebar Filter */}
          <Col md={3} className="mb-4">
            <div className="p-4 shadow-sm rounded bg-white sticky-top" style={{ top: "90px" }}>
              <h5 className="fw-bold mb-4 d-flex align-items-center">
                <i className="fas fa-filter me-2 text-primary"></i> Filters
              </h5>

              {/* Danh m·ª•c */}
              <Form.Group className="mb-3">
                <Form.Label className="fw-semibold">Category</Form.Label>
                <Form.Select
                  value={filters.category}
                  onChange={(e) => setFilters({ ...filters, category: e.target.value })}
                >
                  <option value="">All Categories</option>
                  {categories.map((cat, index) => (
                    <option key={index} value={cat}>{cat}</option>
                  ))}
                </Form.Select>
              </Form.Group>

              {/* Th∆∞∆°ng hi·ªáu (Thay Author -> Brand) */}
              <Form.Group className="mb-3">
                <Form.Label className="fw-semibold">Brand</Form.Label>
                <Form.Select
                  value={filters.brand}
                  onChange={(e) => setFilters({ ...filters, brand: e.target.value })}
                >
                  <option value="">All Brands</option>
                  {brands.map((brand, index) => (
                    <option key={index} value={brand}>{brand}</option>
                  ))}
                </Form.Select>
              </Form.Group>

              {/* Kho·∫£ng gi√° (C·∫≠p nh·∫≠t gi√° Tech) */}
              <Form.Group className="mb-4">
                <Form.Label className="fw-semibold">Price Range</Form.Label>
                <Form.Select
                  value={filters.priceRange}
                  onChange={(e) => setFilters({ ...filters, priceRange: e.target.value })}
                >
                  <option value="">All Prices</option>
                  <option value="low">Under 10 Million</option>
                  <option value="mid">10 - 25 Million</option>
                  <option value="high">Above 25 Million</option>
                </Form.Select>
              </Form.Group>

              <Button
                variant="dark"
                className="w-100"
                onClick={() => setFilters({ category: "", brand: "", priceRange: "" })}
              >
                Reset Filters
              </Button>
            </div>
          </Col>

          {/* üì± Product List */}
          <Col md={9}>
            <div className="d-flex justify-content-between align-items-center mb-4 p-3 bg-white rounded shadow-sm">
              <h4 className="fw-bold m-0">
                All Products <span className="text-muted fs-6">({filteredProducts.length} items)</span>
              </h4>
              <Form.Select
                style={{ width: "200px", border: "1px solid #ced4da" }}
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
              >
                <option value="default">Sort by: Default</option>
                <option value="newest">Newest Arrivals</option>
                <option value="priceAsc">Price: Low to High</option>
                <option value="priceDesc">Price: High to Low</option>
              </Form.Select>
            </div>

            <Row>
              {filteredProducts.length > 0 ? (
                filteredProducts.map((product) => (
                  <Col lg={4} md={6} className="mb-4" key={product.id}>
                    {/* S·ª≠ d·ª•ng component con ProductCard */}
                  </Col>
                ))
              ) : (
                <Col md={12}>
                  <div className="text-center py-5 bg-white rounded shadow-sm">
                    <i className="fas fa-search fa-3x text-muted mb-3"></i>
                    <h5>No products found matching your criteria.</h5>
                    <p className="text-muted">Try removing some filters.</p>
                    <Button
                      variant="outline-primary"
                      onClick={() => setFilters({ category: "", brand: "", priceRange: "" })}
                    >
                      Clear All Filters
                    </Button>
                  </div>
                </Col>
              )}
            </Row>
          </Col>
        </Row>
      </Container>

      <Footer />
    </div>
  );
}

export default ProductList;
